import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import RemoveFromQueueIcon from '@material-ui/icons/RemoveFromQueue';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import ImgMediaCard from './DeleteIcon';

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(10, 7),
        maxWidth: '900px',
        top: '61px !important',
        margin: '0px 0px 0px 16px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },

    root: {
        maxWidth: '900px',
        position: 'relative',
        margin: '60px auto 0 auto',
        marginBottom: '5px',
        border: '1px double #999',
        borderColor: 'black',
    },

    avatar: {
        margin: theme.spacing(1),
        backgroundColor: 'black',
    },
    form: {
        width: '100%',
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
}));

export default function Registration() {
    const classes = useStyles();
    const [state, setState] = useState({
        firstName: '',
        lastName: '',
        email: '',
        mobileNumber: '',
        gender: '',
        submit: [],
        allData: [],
        counterId: 0,
        mode: 'add',
        editedRecord: 0
    }
    )

    function handleRemoval(nodeId) {

        var data = state.allData;
        data = data.filter(function (el) {
            return el.counterId !== nodeId;
        });
        setState({
            ...state,
            allData: data,
            mode: 'add',
            editedRecord: 0
        });
        return;
    }
    function handleEdit(editedRecord) {


        const { allData } = state;
        for (var i in allData) {

            if (allData[i].counterId === editedRecord) {
                setState({
                    ...state,
                    firstName: allData[i].firstName,
                    lastName: allData[i].lastName,
                    email: allData[i].email,
                    mobileNumber: allData[i].mobileNumber,
                    gender: allData[i].gender,
                    mode: 'edit',
                    editedRecord: editedRecord
                });
                break;
            }
        }
        return;
    }
    function myChangeHandler(event) {
        setState({
            ...state,
            [event.target.id]: event.target.value,
        });
    }

    function handleChangeStatus(firstName, nodeId) {
        var data = state.allData;
        for (var i in data) {
            if (data[i].counterId === nodeId) {
            }
        }
    }

    function submitForm(event) {
        event.preventDefault();

        let counter = state.counterId + 1;


        if (state.mode === 'add') {
            setState((state) => ({
                ...state,
                counterId: counter,
                allRecords: [...state.allRecords,
                {
                    counterId: counter,
                    firstName: state.firstName,
                    email: state.email,
                    qualification: state.qualification,
                    mobileNumber: state.mobileNumber,
                    lastName: state.lastName,
                    Status: ''
                },
                ],
                firstName: '',
                lastName: '',
                email: '',
                qualification: '',
                mobileNumber: ''
            }));

        } else {
            const { allRecords } = state;

            for (var i in allRecords) {

                if (allRecords[i].counterId === state.editedRecordId) {
                    allRecords[i].firstName = state.firstName;
                    allRecords[i].lastName = state.lastName;
                    allRecords[i].email = state.email;
                    allRecords[i].mobileNumber = state.mobileNumber;
                    allRecords[i].qualification = state.qualification;
                    break;
                }
            }
            setState((state) => ({
                ...state,
                allRecords: allRecords,
                firstName: '',
                lastName: '',
                email: '',
                qualification: '',
                mobileNumber: '',
                mode: 'add',
                editedRecordId: 0
            }));

        }
        console.log(state);
    }

    return (
        <>
            <Grid container component="main" className={classes.todo_time_comp_con}
                justify="center">
            </Grid>

            <Grid container component="main" className={classes.root}
                justify="center">
                <CssBaseline />
                <Grid item xs={false} sm={12} md={12} ></Grid>
                <Grid item xs={false} sm={4} md={7}>
                    <div className={classes.ImgMediaCardcontainers}>
                        <ImgMediaCard state={state} handleRemoval={handleRemoval} handleChangeStatus={handleChangeStatus} />
                    </div>
                </Grid>
                <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
                    <Container component="main" maxWidth="xs">
                        <CssBaseline />
                        <div className={classes.paper}>
                            <Avatar className={classes.avatar}>
                                <RemoveFromQueueIcon />
                            </Avatar>
                            <Typography component="h1" variant="h5">
                                Registration Form
                                           </Typography>
                            <form className={classes.form} onSubmit={addData}>
                                <Grid container spacing={2}>
                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            autoComplete="fname"
                                            name="firstname"
                                            variant="outlined"
                                            fullWidth
                                            id="firstName"
                                            label="First Name"
                                            onChange={myChangeHandler}

                                        />
                                    </Grid>
                                    <Grid item xs={12} sm={6}>
                                        <TextField
                                            variant="outlined"
                                            fullWidth
                                            id="lastName"
                                            label="Last Name"
                                            name="lastname"
                                            autoComplete="lname"
                                            onChange={myChangeHandler}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            variant="outlined"
                                            fullWidth
                                            id="email"
                                            name="email"
                                            label="Email"
                                            autoComplete="email"
                                            onChange={myChangeHandler}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            variant="outlined"
                                            fullWidth
                                            id="gender"
                                            label="Gender"
                                            name="gender"
                                            autoComplete="gender"
                                            onChange={myChangeHandler}
                                        />
                                    </Grid>
                                    <Grid item xs={12}>
                                        <TextField
                                            variant="outlined"
                                            fullWidth
                                            id="mobileNumber"
                                            name="mobilenumber"
                                            label="Mobile Number"
                                            autoComplete="mobilenumber"
                                            onChange={myChangeHandler}
                                        />
                                    </Grid>
                                </Grid>
                                <Button
                                    type="submit"
                                    fullWidth
                                    variant="contained"
                                    color="primary"
                                    className={classes.submit} >Submit</Button>
                            </form>
                        </div>
                    </Container >
                </Grid>
            </Grid>
        </>
    );
}
