import React, { useState } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import RemoveFromQueueIcon from '@material-ui/icons/RemoveFromQueue';
import { Grid } from '@material-ui/core';
import SimpleTabs from './Tabs';

const useStyles = makeStyles(theme => ({
    paper: {
        marginTop: theme.spacing(8),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },

    root: {
        maxWidth: '800px',
        position: 'relative',
        margin: '61px auto 0 auto',
        marginBottom: '6px'
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: 'black'
    },
    form: {
        width: '100%',
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
}));

export default function SubmitForm() {
    const classes = useStyles();
    const [state, setState] = useState({
        firstName: '',
        lastName: '',
        email: '',
        mobileNumber: '',
        gender: '',
        submit: [],
        dataRecords: []
    })
    function myChangeHandler(event) {
        setState({
            ...state,
            [event.target.id]: event.target.value
        });
        console.log(state);
    }
    function SubmitStudForm(event) {
        event.preventDefault();
        setState({
            ...state,
            dataRecords: [...state.dataRecords,
            {
                firstName: state.firstName,
                lastName: state.lastName,
                email: state.email,
                mobileNumber: state.mobileNumber,
                gender: state.gender
            }
            ]
        });
    }
    return (
        <Container component="main" maxWidth="xs">
            <Grid Container component="main" className={classes.root} justfiy="center">
                <CssBaseline />
                <Grid item xs={false} sm={12} md={12}> </Grid>
                <div className={classes.SimpleTabsContainer}>
                    <SimpleTabs state={state} />
                </div>
                <div className={classes.paper}>
                    <Avatar className={classes.avatar}>
                        <RemoveFromQueueIcon />
                    </Avatar>
                    <Typography component="h1" variant="h5">
                        Submit Form
        </Typography>
                    <form className={classes.form} onSubmit={SubmitStudForm}>
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="firstName"
                            label="First Name"
                            name="firstName"
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="lastName"
                            label="Last Name"
                            name="lastName"
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="email"
                            label="Email"
                            name="email"
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="mobileNumber"
                            label="Mobile Number"
                            id="mobileNumber"
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="gender"
                            label="Gender"
                            id="gender"
                        />
                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="primary"
                            className={classes.submit}
                        >
                            Submit
          </Button>
                    </form>
                </div>

            </Grid>
        </Container >
    );
}
